if(msg.topic == 'output_velocity'){
    var data = msg.payload
    var output_velocity = global.get('output_velocity')

    if(data != output_velocity){
        msg.payload = data[0]
        global.set('output_velocity', msg.payload)
        node.send(msg)
    }
}

if(msg.topic == 'motor_frequency'){
    var data = msg.payload
    data = data[0]*0.1
    data = data.toFixed(2)
    data = parseFloat(data)
    var motor_frequency = global.get('motor_frequency')

    if(data != motor_frequency){
        msg.payload = data
        global.set('motor_frequency', msg.payload)
        node.send(msg)
    }
}

if(msg.topic == 'motor_current'){
    var data = msg.payload
    data = data[0]*0.1
    data = data.toFixed(2)
    data = parseFloat(data)
    var motor_current = global.get('motor_current')

    if(data != motor_current){
        msg.payload = data
        global.set('motor_current', msg.payload)
        node.send(msg)
    }
}

if(msg.topic == 'voltage'){
    var data = msg.payload
    var voltage = global.get('voltage')

    if(data[0] != voltage){
        msg.payload = data[0]
        global.set('voltage', msg.payload)
        node.send(msg)
    }
}

if(msg.topic == 'overload_fault_duration'){
    var data = msg.payload
    var buf = Buffer.from([data.buffer[0],data.buffer[1]])
    var res = buf.readInt16BE()
    msg.payload = res
    global.set('overload_fault_duration', msg.payload)
    node.send(msg)
}

if(msg.topic == 'speed'){
    var data = msg.payload
    var buf = Buffer.from([data.buffer[0],data.buffer[1]])
    var res = buf.readInt16BE()
    res = (res*3)/2
    res = parseInt(res)

    var speed_setpoint = global.get('speed_setpoint')

    if(res <= (speed_setpoint + 1) && res >= (speed_setpoint - 1)){
        global.set('speed', speed_setpoint)
    }
    else{
        msg.payload = speed_setpoint
        global.set('speed', msg.payload)
        node.send(msg)
    }
}

if(msg.topic == 'acceleration_time'){
